mapper.update:
update SUB_SECTION_SUMMARY set MODIFIED_DATE = #{MODIFIED_DATE}, MODIFIED_BY= #{MODIFIED_BY}, SUB_SECTION_STATUS = #{asStatus}  
WHERE ORGANIZATION_ID = #{orgId}
AND SUB_SECTION_ID = #{subSecId}
AND SECTION_ID = #{sectionId}
    
      AND FORM_NAME = #{formName}
      AND FORM_VERSION = #{formVersion}
    
    
      AND BUSINESS_APPLICATION_ID = #{bussAppId}

mapper.select:
SELECT FORM_ID, FORM_NAME, FORM_VERSION FROM SUB_SECTION_SUMMARY 
WHERE ORGANIZATION_ID = #{asOrgId} AND BUSINESS_APPLICATION_ID = #{asBuisAppId} 
AND SECTION_ID = #{section} AND SUB_SECTION_ID = #{subSection}
AND rownum = 1
